import React from 'react';

import './form-input.styles.scss';

const FormInput = ({ handleChange, label, ...otherProps }) => (
  <div className="group">
    {
      label ?
        <input
          className={otherProps.value.length > 0 ? 'shrink' : ''}
          onChange={handleChange}
        />
        : null
    }
  </div>
);
