import { createSelector } from "reselect";

const COLLECTIONS_ID_MAP = {
  hats: 1,
  jackets: 2,
  sneakers: 3,
  womens: 4,
  mens: 5
};

const selectShop = state => state.shop;

export const selectCollections = createSelector(
  [selectShop],
  shop => shop.collections
);

export const selectCollection = searchUrlParam =>
  createSelector(
    [selectCollections],
    collections => {
      console.log(COLLECTIONS_ID_MAP[searchUrlParam])
      collections.find(collection => collection.id === COLLECTIONS_ID_MAP[searchUrlParam]);
    }
  )
