import { createSelector } from "reselect";

const COLLECTIONS_MAP_TO_ID = {
  hats: 1,
  jackets: 2,
  sneakers: 3,
  womens: 4,
  mens: 5
};

const selectShop = state => state.shop;

export const selectCollections = createSelector(
  [selectShop],
  shop => shop.collections
);

export const selectCollection = searchUrlParam =>
  createSelector(
    [selectCollections],
    collections => collections.find(collection => collection.id === COLLECTIONS_MAP_TO_ID[searchUrlParam])
  )
